DROP TABLE IF EXISTS `adinfo`;
CREATE TABLE `adinfo` (  `id` smallint(6) unsigned NOT NULL auto_increment,  `available` tinyint(1) NOT NULL default '1',  `title` varchar(50) character set utf8 NOT NULL,  `image` varchar(60) character set utf8 NOT NULL,  `link` char(120) character set utf8 default NULL,  `displayorder` smallint(5) unsigned NOT NULL default '0',  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=latin1;
DROP TABLE IF EXISTS `ask`;
