DROP TABLE IF EXISTS `card`;
CREATE TABLE `card` (  `id` varchar(16) NOT NULL,  `code` varchar(16) default NULL,  `partner_id` int(10) unsigned NOT NULL default '0',  `team_id` int(10) unsigned NOT NULL default '0',  `order_id` int(10) unsigned NOT NULL default '0',  `credit` int(10) unsigned NOT NULL default '0',  `consume` enum('Y','N') NOT NULL default 'N',  `ip` varchar(16) default NULL,  `begin_time` int(10) unsigned NOT NULL default '0',  `end_time` int(10) unsigned NOT NULL default '0',  PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `cart`;
CREATE TABLE `cart` (  `id` bigint(20) unsigned NOT NULL auto_increment,  `user_id` int(10) unsigned NOT NULL default '0',  `admin_id` int(10) unsigned NOT NULL default '0',  `detail` text,  `money` double(10,2) NOT NULL default '0.00',  `action` varchar(16) NOT NULL default 'buy',  `state` varchar(16) NOT NULL default 'unpay',  `create_time` int(10) unsigned NOT NULL default '0',  PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `category`;
