DROP TABLE IF EXISTS `coupon`;
CREATE TABLE `coupon` (  `id` varchar(16) NOT NULL default '',  `user_id` int(10) unsigned NOT NULL default '0',  `partner_id` int(10) unsigned NOT NULL default '0',  `team_id` int(10) unsigned NOT NULL default '0',  `order_id` int(10) unsigned NOT NULL default '0',  `type` enum('consume','credit') NOT NULL default 'consume',  `credit` int(10) unsigned NOT NULL default '0',  `secret` varchar(10) default NULL,  `consume` enum('Y','N') NOT NULL default 'N',  `ip` varchar(16) default NULL,  `sms` int(10) unsigned NOT NULL default '0',  `expire_time` int(10) unsigned NOT NULL default '0',  `consume_time` int(10) unsigned NOT NULL default '0',  `create_time` int(10) unsigned NOT NULL default '0',  `sms_time` int(10) unsigned NOT NULL default '0',  `buy_id` int(11) NOT NULL default '0',  PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `credit`;
CREATE TABLE `credit` (  `id` bigint(20) unsigned NOT NULL auto_increment,  `user_id` int(10) unsigned NOT NULL default '0',  `admin_id` int(10) unsigned NOT NULL default '0',  `detail_id` varchar(32) default NULL,  `detail` varchar(255) default NULL,  `score` double(10,2) NOT NULL default '0.00',  `action` varchar(16) NOT NULL default 'buy',  `rname` varchar(32) default NULL,  `rmobile` varchar(32) default NULL,  `rcode` char(6) default NULL,  `raddress` varchar(128) default NULL,  `send_time` int(10) default NULL,  `create_time` int(10) unsigned NOT NULL default '0',  `state` enum('unpay','pay') NOT NULL default 'unpay',  `remark` text,  `op_time` int(11) NOT NULL default '0',  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `daysign`;
