DROP TABLE IF EXISTS `goods`;
CREATE TABLE `goods` (  `id` int(11) NOT NULL auto_increment,  `title` varchar(128) default NULL,  `score` int(11) NOT NULL default '0',  `image` varchar(128) default NULL,  `time` int(11) NOT NULL default '0',  `number` int(11) NOT NULL default '0',  `per_number` int(11) NOT NULL default '1',  `sort_order` int(11) NOT NULL default '0',  `consume` int(11) NOT NULL default '0',  `display` enum('Y','N') NOT NULL default 'Y',  `enable` enum('Y','N') NOT NULL default 'Y',  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `invite`;
CREATE TABLE `invite` (  `id` bigint(20) unsigned NOT NULL auto_increment,  `user_id` int(10) unsigned NOT NULL default '0',  `admin_id` int(10) unsigned NOT NULL default '0',  `user_ip` varchar(16) default NULL,  `other_user_id` int(10) unsigned NOT NULL default '0',  `other_user_ip` varchar(16) default NULL,  `team_id` int(10) unsigned NOT NULL default '0',  `pay` enum('Y','N','C') NOT NULL default 'N',  `credit` int(10) unsigned NOT NULL default '0',  `buy_time` int(10) unsigned NOT NULL default '0',  `create_time` int(10) unsigned NOT NULL default '0',  PRIMARY KEY  (`id`),  UNIQUE KEY `UNQ_uo` (`user_id`,`other_user_id`),  UNIQUE KEY `UNQ_o` (`other_user_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `logger_admin`;
