DROP TABLE IF EXISTS `mailer`;
CREATE TABLE `mailer` (  `id` bigint(20) unsigned NOT NULL auto_increment,  `email` varchar(128) default NULL,  `city_id` int(10) unsigned NOT NULL default '0',  `secret` varchar(32) default NULL,  PRIMARY KEY  (`id`),  UNIQUE KEY `UNQ_e` (`email`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (  `id` bigint(20) unsigned NOT NULL auto_increment,  `title` varchar(128) default NULL,  `detail` text,  `begin_time` int(10) unsigned NOT NULL default '0',  PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `order`;
