_time` int(10) unsigned NOT NULL default '0',  `seo_title` varchar(255) default NULL,  `seo_keyword` varchar(255) default NULL,  `seo_description` text,  `express_relate` text,  `sub_id` int(10) NOT NULL default '0',  `task_next_time` int(10) unsigned NOT NULL default '0',  `task_i_time` int(10) unsigned NOT NULL default '0',  `task_i_num` int(10) unsigned NOT NULL default '0',  `task_state` tinyint(1) unsigned NOT NULL default '1',  `area_ids` varchar(255) NOT NULL,  `is_hdfk` tinyint(1) unsigned NOT NULL default '0',  `team_tags` varchar(225) NOT NULL default '0',  `send_time` int(10) default '0',  `area_id` int(10) unsigned NOT NULL default '0',  `subarea_ids` text,  `discount` double(10,2) default NULL,  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=55 DEFAULT CHARSET=utf8;
